mapper.update:
UPDATE sys_dept SET
parent_id = #{parentId}, dept_name = #{deptName}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, status = #{status}
WHERE dept_id = #{deptId}

mapper.select:
select *
from sys_dept
where dept_name = #{deptName}
  and parent_id = #{parentId}
