mapper.select:
select * from  sys_user


        and user_id = #{sysUser.userId}


        and user_name like concat('%', #{sysUser.userName}, '%')



        and nick_name  like concat('%',#{sysUser.nickName},'%')


        and dept_id = #{sysUser.deptId}


        and phonenumber = #{sysUser.phonenumber}


        and status =#{sysUser.status}


        and DATE_FORMAT(create_time, '%y%m%d') > DATE_FORMAT(#{sysUser.createTime}, '%y%m%d')


limit #{index},#{size}
