select sys_user.user_id, sys_user.dept_id, sys_user.user_name, sys_user.nick_name, sys_user.sex, sys_user.email, sys_user.remark, sys_dept.dept_name, sys_user.phonenumber, sys_user.status, sys_user.create_time, sys_user_post.post_id, sys_role.role_id from sys_user left join sys_dept on sys_user.dept_id = sys_dept.dept_id left join sys_user_role on sys_user_role.user_id = sys_user.user_id left join sys_role on sys_user_role.role_id = sys_role.role_id left join sys_user_post on sys_user_post.user_id = sys_user.user_id left join sys_post on sys_post.post_id = sys_user_post.post_id =sys_post.post_id
sys_user.user_id != 1 and sys_user.del_flag =0
and sys_user.dept_id = #{sysUser.deptId}
and sys_user.user_name like concat('%', #{sysUser.userName}, '%')
and sys_user.phonenumber like concat('%', #{sysUser.phonenumber}, '%')
and sys_user.status = #{sysUser.status}
and DATE_FORMAT(sys_user.create_time, '%y%m%d') > DATE_FORMAT(#{sysUser.createTime}, '%y%m%d')
limit #{index},#{size}

